mapper.select:
select * from article where is_delete = 0

    and id = #{articleId,jdbcType=VARCHAR}

  ORDER BY create_time DESC LIMIT 1

select art.id,art.sketch,art.back_img,art.title,art.category_id,art.read_num, art.author_name,art.create_time,art.update_time,(select count(*) from article_like where article_id=art.id) as like_num, (select count(*) from article_comment where article_id=art.id) as comment_num from article art where art.is_delete=0 and art.category_id ='1' and art.is_subject = 0
and title like #{key,jdbcType=VARCHAR}
ORDER BY update_time DESC
